queries["guardarOrdenSuministro"] = "INSERT INTO owmedicamentos.orden_suministro(numero_orden_suministro, id_institucion_requirente, id_contrato, fecha_expedicion_orden, fecha_entrega_orden, id_proveedor, id_medicamento, cantidad_solicitada, id_almacen_entrega, id_clues, origen, usuario_carga) SELECT %s,%s,(Select id from ca_contratos where contrato = %s and id_dependencia = %s), coalesce((Select fecha_expedicion_orden from orden_suministro where numero_orden_suministro = %s and id_institucion_requirente = %s Limit 1) , coalesce(%s, now())), coalesce((Select fecha_entrega_orden from orden_suministro where numero_orden_suministro = %s and id_institucion_requirente = %s Limit 1) , coalesce(%s,CURRENT_DATE + INTERVAL '14 day')), (select id from ca_proveedores where rfc = %s and estatus = true),(select id from ca_medicamentos where clave = %s),%s,(Select id from ca_almacenes where codigo = %s and estatus = true),(Select id from ca_clues where clave = %s and id_dependencia = %s and estatus = true),1, %s WHERE NOT EXISTS (Select 1 from orden_suministro where numero_orden_suministro = %s and estatus NOT IN (1,9) and id_institucion_requirente = %s)  RETURNING id_orden_suministro, numero_orden_suministro; "
queries["datosValidacionOrdenSuministro"] = "select rcm.id, rcm.cantidad_disponible from ca_contratos cc join ca_dependencias cd on cd.id = cc.id_dependencia and cd.estatus = true and cd.id = %s join ca_clues cc2 on cc2.id_dependencia = cd.id and cc2.estatus = true and cc2.clave = %s join ca_proveedores cp on cp.id = cc.id_proveedor and cp.estatus = true join rel_contrato_medicamentos rcm on rcm.id_contrato = cc.id and (rcm.cantidad_disponible - %s) >= 0 join ca_medicamentos cm on cm.id = rcm.id_medicamento and cm.estatus = true join ca_accesos ca on ca.id_dependencia = cd.id and ca.estatus = true join rel_accesos_modulos ram on ram.id_usuario = ca.id where ca.usuario_htpasswd = %s and ram.id_modulo_sistema = 102 and ca.id_rol = 3 and rcm.id_contrato = (select id from ca_contratos cc3 where cc3.contrato = %s and cc3.id_dependencia = %s) and %s::timestamp::date between cc.fecha_ini::timestamp::date and cc.fecha_fin::timestamp::date and cp.rfc = %s and cm.clave = %s group by rcm.id, rcm.cantidad_disponible limit 1;"
queries["actualizarCantidadDisponibleOS"] = "UPDATE owmedicamentos.rel_contrato_medicamentos set cantidad_disponible = coalesce(coalesce (cantidad_disponible - %s, null), cantidad_disponible) where id = %s returning id;"
queries["obtenerMensajeErrorOS"] = "select %s as in_dependencia, %s as in_clave, %s as in_contrato, %s as in_cantidad_solicitada, %s as in_fecha_expedicion, %s in_rfc, %s as in_clave_medicamento, %s as in_usuario, %s as in_numero_os, %s as in_almacen_entrega, %s as in_fecha_ent, rcm.id rel_contrato_med,rcm.cantidad_disponible, case when (rcm.cantidad_disponible - %s) >= 0 then null else 'La cantidad solicitada supera la cantidad disponible' end as cantidad_disponible_message, cc.fecha_ini, cc.fecha_fin, case when %s between cc.fecha_ini and cc.fecha_fin then null else 'La fecha no es vlida' end as fecha_message,cp.rfc,case when cp.rfc <> %s then 'El rfc es invlido' else null end rfc_message, cm.clave, case when cm.clave <> %s then 'La clave de medicamento no es vlida' else null end clave_med_message from ca_contratos cc join ca_dependencias cd on cd.id = cc.id_dependencia and cd.estatus = true  and cd.id = %s join ca_clues cc2 on cc2.id_dependencia = cd.id and cc2.estatus = true  and cc2.clave = %s join ca_proveedores cp on cp.id = cc.id_proveedor and cp.estatus = true join rel_contrato_medicamentos rcm on rcm.id_contrato = cc.id join ca_medicamentos cm on cm.id = rcm.id_medicamento and cm.estatus = true join ca_accesos ca on ca.id_dependencia = cd.id and ca.estatus = true join rel_accesos_modulos ram on ram.id_usuario = ca.id where ca.usuario_htpasswd = %s and ram.id_modulo_sistema = 102 and ca.id_rol = 3 and cc.contrato = %s order by rel_contrato_med, cantidad_disponible_message, fecha_message, rfc_message, clave_med_message; "
queries["validacionesCargaOS"] = "select %s as in_dependencia, %s as in_contrato, %s as in_usuario, %s as in_numero_os, case when (select distinct ca.id_dependencia from ca_dependencias cd  join ca_accesos ca on ca.id_dependencia = cd.id where  cd.estatus = true and ca.usuario_htpasswd = %s) = %s then null else 'La dependencia es invlida o est inactiva. ' end as dependencia_message, case when (select distinct contrato from ca_contratos cc join ca_dependencias cd on cd.id = cc.id_dependencia and cd.estatus = true and cd.id = %s join ca_accesos ca on ca.id_dependencia = cd.id and ca.estatus = true join rel_accesos_modulos ram on ram.id_usuario = ca.id and ram.id_modulo_sistema = 102 where cc.estatus = true and cc.contrato = %s and cc.fecha_fin >= current_date and ca.usuario_htpasswd = %s) = %s then null else 'El contrato es invlido, est inactivo o no es vigente. ' end as contrato_message, case when (select distinct cc2.clave from ca_dependencias cd join ca_clues cc2 on cc2.id_dependencia = cd.id and cc2.estatus = true and cc2.clave = %s where  cd.estatus = true and cd.id = %s) = %s then null else 'La clues es invlida o est inactiva. ' end as clues_message,case when (select distinct cp.rfc from ca_contratos cc join ca_dependencias cd on cd.id = cc.id_dependencia and cd.estatus = true  and cd.id = %s join ca_proveedores cp on cp.id = cc.id_proveedor and cp.estatus = true where cc.contrato = %s and cp.rfc = %s) = %s then null else 'El rfc del proveedor es invlido o est inactivo. ' end as rfc_message,case when (select %s between cc.fecha_ini::timestamp::date and cc.fecha_fin::timestamp::date from ca_contratos cc join ca_dependencias cd on cd.id = cc.id_dependencia and cd.estatus = true  and cd.id = %s where cc.estatus = true and cc.contrato = %s) is true then null else 'Lafechadeexpedicinnoesvlida, verificar la vigencia delcontrato. ' end as fecha_message, case when (select distinct cm.clave from ca_contratos cc join rel_contrato_medicamentos rcm on rcm.id_contrato = cc.id join ca_medicamentos cm on cm.id = rcm.id_medicamento and cm.estatus = true where cc.estatus = true and cc.contrato = %s and cm.clave = %s) = %s then null else 'La clave del medicamento es invlido o est inactivo. ' end as medicamento_message,case when (select rcm.cantidad_disponible - %s from ca_contratos cc join rel_contrato_medicamentos rcm on rcm.id_contrato = cc.id join ca_medicamentos cm on cm.id = rcm.id_medicamento and cm.estatus = true where cc.estatus = true and cc.contrato = %s and cm.clave = %s and cc.id_dependencia = %s) >= 0 then null else 'La cantidad solicitada rebasa la cantidad disponible. ' end as cantidad_sol_message,case when (select ca2.codigo from ca_almacenes ca2 where ca2.estatus = true and codigo = %s) = %s then null else 'El almacn es invlido o est inactivo' end as almacen_message;"
queries["validacionUsuarioOS"] = "select ca.estatus, ca.id_rol, ram.id_modulo_sistema from ca_accesos ca join rel_accesos_modulos ram on ram.id_usuario = ca.id where ca.usuario_htpasswd = %s and id_modulo_sistema = 102;"
